// PathfindingVisualizer.jsx
import React, { useState, useRef , useEffect } from "react";
import "./Sorting.css";
import { executeAlgorithm } from "../algorithms";
import Node from "./Node";
import Toolbar from "../utils/ToolBar";
import { useGridHandler } from "../hooks/useGridHandler";
import { useVisualization } from "../hooks/useVisualization";
import { useMazeGenerator } from "../hooks/useMazeGenerator";
import { initialGrid, findStartNode, findFinishNode, initialGrid2 } from "../utils/GridUtils";
import { dijkstra } from "../algorithms/dijkstra";
import { dfs } from "../algorithms/dfs";
import { bfs } from "../algorithms/bfs";
import { astar } from "../algorithms/astar";
import { gbfs } from "../algorithms/gbfs";
import { bidirectional } from "../algorithms/bidirectional";
import AlgorithmStats from "./algorithmStats";
import Description from "./Description";


const algorithms = [
  { label: "Dijkstra", actionKey: "dijkstra", func: dijkstra },
  { label: "A*", actionKey: "astar", func: astar },
  { label: "Greedy Best First Search", actionKey: "gbfs", func: gbfs },
  {
    label: "Bidirectional Search",
    actionKey: "bidirectional",
    func: bidirectional,
  },
  { label: "Breadth First Search", actionKey: "bfs", func: bfs },
  { label: "Depth First Search", actionKey: "dfs", func: dfs },
];

const mazeOptions = [
  { label: "Randomize Board", actionKey: "randomizeBoard" },
  {
    label: "Recursive Division Maze",
    actionKey: "generateRecursiveDivisionMaze",
  },
];


const Sorting = () => {

  const [isModalOpen, setIsModalOpen] = useState(true); // Modal is open by default
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  // Function to update the window width state
  const handleResize = () => {
    setWindowWidth(window.innerWidth);
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const shouldShowModal = windowWidth > 768 && isModalOpen;

  const [grid, setGrid] = useState(initialGrid());
  const { handleMouseDown, handleMouseEnter, handleMouseUp } = useGridHandler(
    grid,
    setGrid
  );
  const { visualize, clearBoard, resetForVisualization, resetForMaze } =
    useVisualization(grid, setGrid);

  const {
    randomizeBoard,
    generateWeightedMaze,
    generateRecursiveDivisionMaze,
  } = useMazeGenerator(grid, setGrid);


  const [selectedAlgorithm, setSelectedAlgorithm] = useState(algorithms[0]); // Default to Dijkstra object

  const handleToolbarAction = (actionKey) => {
    switch (actionKey) {
      case "clearBoard":
        clearBoard();
        break;
      case "clearPath":
        resetForVisualization();
        break;
      case "randomizeBoard":
        resetForMaze();
        randomizeBoard(); // Assuming this generates a random maze
        break;
      case "generateRecursiveDivisionMaze":
        resetForMaze();
        generateRecursiveDivisionMaze();
        break;
      case "generateWeightedMaze":
        resetForMaze();
        generateWeightedMaze();
        break;
      case "visualize":
        resetForVisualization();
        startVisualization(selectedAlgorithm.actionKey);
        break;
      default:
        if (actionKey.startsWith("algo-")) {
          const algorithmKey = actionKey.replace("algo-", "");
          const selected = algorithms.find(
            (algo) => algo.actionKey === algorithmKey
          );
          if (selected) {
            setSelectedAlgorithm(selected);
          }
        }
        break;
    }
  };

  const startVisualization = () => {
    const startNode = findStartNode(grid);
    const finishNode = findFinishNode(grid);
    const algorithmFunc = selectedAlgorithm.func;

    if (!algorithmFunc) {
      console.error(`Algorithm function not found.`);
      return;
    }

    const result = executeAlgorithm(algorithmFunc, grid, startNode, finishNode);


    if (
      !result ||
      !result.visitedNodesInOrder ||
      !result.nodesInShortestPathOrder
    ) {
      console.error("Algorithm did not return expected result.");
      return; // Exit to avoid calling visualize with undefined values
    }



    visualize(result.visitedNodesInOrder, result.nodesInShortestPathOrder);
  };

  return (
    <div className="main">
      <Toolbar
        onAction={handleToolbarAction}
        selectedAlgorithm={selectedAlgorithm.label}
        algorithmItems={algorithms.map((algo) => ({
          label: algo.label,
          actionKey: `algo-${algo.actionKey}`,
        }))}
        mazeItems={mazeOptions} // Pass the maze options here
        // isWeightedGraph={isWeightedGraph}
        ispathfinderEnabled={false}
      />

      {shouldShowModal && (
          <div className="modal">
            <Description />
          </div>
      )}


      <section className="inner-page">


        <section id="overview" className="pt-0">
          <div className="container">
            <h3 className="highlighted-text">Overview</h3>
            <div className="mb-4 explanation ml-2 mr-2" style={{ fontSize: "large" }}>
              A sorting algorithm is an algorithm that puts elements of a list
              in a certain order (thus sorting the list). The most frequently
              used orders are numerical order for lists of numbers and
              lexicographical order for lists of strings.
              <br />
              <br />
              To put it more formally, the output generally has to fulfill two
              conditions:
              <ul>
                <li>The output is in nondecreasing order</li>
                <li>The output is a permutation of the input</li>
              </ul>
              Or, to put it simply, when running a sorting algorithm we expect
              an output that contains all the elements originally present in the
              input arranged in such a way that the smallest element (according
              to the operation used to sort the list) is in the leftmost place,
              with every element following being either bigger or equal to its
              predecessor.
              <br />
              <br />
              Sorting algorithms can generally be classified into three distinct
              categories:
              <ul>
                <li>Comparison sorts</li>
                <li>Non-comparison sorts</li>
                <li>Others</li>
              </ul>
              On this page you can find many implementations for every category.
              If you need a visualization for a certain algorithm, just use the
              search function to find it quickly.
            </div>
          </div>
          <div className="container-fluid">
            <div className="row mt-5 highlighted-text">
              <div className="col-sm-12 text-center">
                <h2>Search</h2>
              </div>
            </div>
            <div className="row justify-content-center mt-2">
              <div className="col-sm-12 col-md-10 col-lg-8">
                <form className="card card-sm border-red">
                  <div className="card-body row no-gutters align-items-center">
                    <div className="col-auto">
                      <i className="bx bx-search h4 text-body"></i>
                    </div>
                    {/*end of col*/}
                    <div className="col">
                      <input
                        id="search"
                        className="form-control form-control-borderless"
                        type="search"
                        placeholder="Search names or keywords"
                      />
                    </div>
                    <div className="anchor w-100" id="comparison"></div>
                    {/*end of col*/}
                    {/*end of col*/}
                  </div>
                </form>
              </div>
              {/*end of col*/}
            </div>
          </div>
        </section>

        <section id="comparison-sorts" className="pt-0">
        <div className="container-fluid w-100">
          {/* Gradient divider */}
          <hr data-content="Comparison Sorts" className="hr-text mb-2 cd-hide" />
          <div className="w-100 p-2 row mt-3 mb-3 cd-hide">
            <div className="col-sm-1 col-md-1 col-lg-3"></div>
            <div className="explanation col-sm-10 col-md-10 col-lg-6">
              A comparison sort is a type of sorting algorithm that only reads
              the list elements through a single abstract comparison operation
              (often a "less than or equal to" operator or a three-way
              comparison) that determines which of two elements should occur
              first in the final sorted list.
            </div>
            <div className="col-sm-1 col-md-1 col-lg-3"></div>
          </div>
          <div className="card-deck mb-4 cd-hide">
            <div id="bubblesort" className="algorithm shadow-highlight card">
              <a href="bubblesort.html" className="unstyled-link">
                <div className="card-body">
                  <h5 className="card-title">Bubblesort</h5>
                  <h6 className="card-subtitle">Comparison sort</h6>
                  <p className="card-text">
                    Bubblesort is a simple sorting algorithm that repeatedly
                    steps through the list, compares adjacent elements and
                    swaps them if they are in the wrong order. The pass
                    through the list is repeated until the list is sorted.
                    <br />
                    <br />
                    <b>Best-case:</b>&emsp;&emsp;<i> O(n)</i>
                    <br />
                    <b>Average-case:</b> <i>O(n)</i> <br />
                    <b>Worst-case:</b>&emsp; <i>O(n)</i>
                    <br />
                  </p>
                </div>
              </a>
            </div>
            <div
              id="cocktailshakersort"
              className="algorithm shadow-highlight card"
            >
              <a href="cocktailshakersort.html" className="unstyled-link">
                <div className="card-body">
                  <h5 className="card-title">Cocktail shaker sort</h5>
                  <h6 className="card-subtitle">Comparison sort</h6>
                  <p className="card-text">
                    Cocktail shaker sort is an extension of bubble sort. It
                    extends bubble sort by operating in two directions. While
                    it improves on bubble sort by more quickly moving items to
                    the beginning of the list, it provides only marginal
                    performance improvements.
                    <br />
                    <br />
                    <b>Best-case:</b>&emsp;&emsp;<i> O(n)</i>
                    <br />
                    <b>Average-case:</b> <i>O(n)</i> <br />
                    <b>Worst-case:</b>&emsp; <i>O(n)</i>
                    <br />
                  </p>
                </div>
              </a>
            </div>
            <div id="combsort" className="algorithm shadow-highlight card">
              <a href="combsort.html" className="unstyled-link">
                <div className="card-body">
                  <h5 className="card-title">Combsort</h5>
                  <h6 className="card-subtitle">Comparison sort</h6>
                  <p className="card-text">
                    Combsort is an extension of bubble sort. It compares
                    elements divided by a gap which shrinks after each pass
                    through the list. This helps move smaller elements to the
                    beginning of the list faster, which is a main problem of
                    bubble sort.
                    <br />
                    <br />
                    <b>Best-case:</b>&emsp;&emsp;<i> O(n log n)</i>
                    <br />
                    <b>Average-case:</b> <i>O(n)</i> <br />
                    <b>Worst-case:</b>&emsp; <i>O(n)</i>
                    <br />
                  </p>
                </div>
              </a>
            </div>
            <div id="gnomesort" className="algorithm shadow-highlight card">
              <a href="gnomesort.html" className="unstyled-link">
                <div className="card-body">
                  <h5 className="card-title">Gnomesort</h5>
                  <h6 className="card-subtitle">Comparison sort</h6>
                  <p className="card-text">
                    The gnome sort is a sorting algorithm which is similar to
                    insertion sort in that it works with one item at a time
                    but gets the item to the proper place by a series of
                    swaps, similar to a bubble sort.
                    <br />
                    <br />
                    <b>Best-case:</b>&emsp;&emsp;<i> O(n)</i>
                    <br />
                    <b>Average-case:</b> <i>O(n)</i> <br />
                    <b>Worst-case:</b>&emsp; <i>O(n)</i>
                    <br />
                  </p>
                </div>
              </a>
            </div>
          </div>
          <div className="card-deck mb-4 cd-hide">
            <div id="insertionsort" className="algorithm shadow-highlight card">
              <a href="insertionsort.html" className="unstyled-link">
                <div className="card-body">
                  <h5 className="card-title">Insertionsort</h5>
                  <h6 className="card-subtitle">Comparison sort</h6>
                  <p className="card-text">
                    Insertion sort is a simple sorting algorithm that builds
                    the final sorted array (or list) one item at a time. It is
                    much less efficient on large lists than more advanced
                    algorithms such as quicksort, heapsort, or merge sort.
                    <br />
                    <br />
                    <b>Best-case:</b>&emsp;&emsp;<i> O(n)</i>
                    <br />
                    <b>Average-case:</b> <i>O(n)</i> <br />
                    <b>Worst-case:</b>&emsp; <i>O(n)</i>
                    <br />
                  </p>
                </div>
              </a>
            </div>
            <div
              id="mergesort"
              className="algorithm shadow-highlight card"
            >
              <a href="mergesort.html" className="unstyled-link">
                <div className="card-body">
                  <h5 className="card-title">Mergesort</h5>
                  <h6 className="card-subtitle">Comparison sort</h6>
                  <p className="card-text">
                    Mergesort is an efficient, stable, general-purpose,
                    comparison-based sorting algorithm. Most implementations
                    produce a stable sort, meaning that the order of equal
                    elements is the same in the input and output.
                    <br />
                    <br />
                    <b>Best-case:</b>&emsp;&emsp;<i> O(n log n)</i>
                    <br />
                    <b>Average-case:</b> <i>O(n log n)</i> <br />
                    <b>Worst-case:</b>&emsp; <i>O(n log n)</i>
                    <br />
                  </p>
                </div>
              </a>
            </div>
            <div id="selectionsort" className="algorithm shadow-highlight card">
              <a href="selectionsort.html" className="unstyled-link">
                <div className="card-body">
                  <h5 className="card-title">Selectionsort</h5>
                  <h6 className="card-subtitle">Comparison sort</h6>
                  <p className="card-text">
                    Selection sort is a simple comparison sorting algorithm.
                    It works by dividing the input list into two parts: the
                    sorted part and the unsorted part. The algorithm
                    repeatedly selects the smallest (or largest, depending on
                    sorting order) element from the unsorted part and moves it
                    to the end of the sorted part.
                    <br />
                    <br />
                    <b>Best-case:</b>&emsp;&emsp;<i> O(n)</i>
                    <br />
                    <b>Average-case:</b> <i>O(n)</i> <br />
                    <b>Worst-case:</b>&emsp; <i>O(n)</i>
                    <br />
                  </p>
                </div>
              </a>
            </div>
          </div>
        </div>
      </section>











      </section>





    </div>
  );
};

