import React, { useState } from "react";
import "./ToolBar.css"; // Ensure your CSS file is set up for styling

// Button component
const Button = ({ label, onClick, className }) => (
  <button className={`button ${className}`} onClick={onClick}>
    {label}
  </button>
);

// DropdownMenu component
const DropdownMenu = ({ label, items, onActionSelect }) => (
  <div className="dropdown">
    <button className="dropbtn">{label}</button>
    <div className="dropdown-content">
      {items.map((item, index) => (
        <a
          key={index}
          href="#"
          onClick={(e) => {
            e.preventDefault();
            onActionSelect(item.actionKey);
          }}
        >
          {item.label}
        </a>
         
      ))}
    </div>
  </div>
);

// Sidebar component
const Sidebar = ({ isVisible, onClose }) => {
  return (
    <div className={`sidebar ${isVisible ? "visible" : ""}`}>
      <button className="close-btn" onClick={onClose}>✖</button>
      <ul>
        <li>Option 1</li>
        <li>Option 2</li>
        <li>Option 3</li>
        {/* Add more items as needed */}
      </ul>
    </div>
  );
};

// Toolbar component accepting onAction as a prop for handling action selection
const Toolbar = ({
  onAction,
  selectedAlgorithm,
  algorithmItems,
  mazeItems,
  isWeightedGraph,
}) => {

  const [isSidebarVisible, setIsSidebarVisible] = useState(false);



  // Toolbar configuration, potentially fetched or modified dynamically
  let toolbarItems = [
   
    {
      type: "button",
      label: "Clear Board",
      actionKey: "clearBoard",
      className: "clear-board-button",
    },
    {
      type: "button",
      label: "Clear Path",
      actionKey: "clearPath",
      className: "clear-path-button",
    },
    {
      type: "dropdown",
      label: "Select Maze ▼",
      items: mazeItems,
      className: "maze-dropdown",
    },
    {
      type: "dropdown",
      label: "Select Algorithm ▼",
      items: algorithmItems,
      className: "algorithm-dropdown",
    },
    {
      type: "button",
      label: `Visualize ${selectedAlgorithm}`,
      actionKey: "visualize",
      className: "visualize-button",
    },

  ];

  return (  
      <div className="toolbar">
         <Button
            label="☰"
            className="sidebar-toggle-button"
            onClick={() => setIsSidebarVisible(!isSidebarVisible)}
      />
      <div className="toolbar1">
        {toolbarItems.map((item, index) => {
          switch (item.type) {
            case "button":
              return (
                <Button
                  key={index}
                  label={item.label}
                  onClick={() => onAction(item.actionKey)}
                  className={item.className}
                />
              );
            case "dropdown":
              return (
                <DropdownMenu
                  key={index}
                  label={item.label}
                  items={item.items}
                  onActionSelect={(actionKey) => {
                    onAction(actionKey);
                  }}
                />
              );
            default:
              return null;
          }
        })}

      </div>


        {isSidebarVisible && (
          <Sidebar
            isVisible={isSidebarVisible}
            onClose={() => setIsSidebarVisible(false)} // Close sidebar when ✖ clicked
          />
        )}
        </div>   
  );
};

export default Toolbar;
